INSERT INTO c_content_master VALUES("10","Mission,Vision And Values","<h2>Our Vision:</h2>\n        <p><strong>To be a law enforcement agency of repute.</strong></p>\n        <p>Aspiration to provide a dignified service worthy of honour, a service   that value relations with members of the community and treats customers   with due respect</p>\n        \n        <h2>Our Mission:</h2>\n        <p>To provide a professional policing service in partnership with the community</p>\n        <p>A service characterised by a high level of skill, competence, speed   and attention to any emerging policing challenges, and that recognizes   community participation for better prevention and detection of crime.</p>\n        \n        <h2>Our Core Values</h2>\n        <p><strong>Botho:</strong></p>\n        <p>A service characterized by humility, dignity, courtesy and respect for customers, regardless of their status in life.</p>\n        <p><strong>Excellence</strong></p>\n        <p>An acceptable level of accuracy on all services provided to customers.</p>\n        <p><strong>Integrity</strong></p>\n        <p>Displaying ethical behavior that recognizes the need for confidentiality, respect for human rights and good practice</p>\n        <p><strong>Team work</strong></p>\n        <p>A service characterized by consultation, collective responsibility and support for each other.</p> \n        \n        <h2>Mandate</h2>\n      <p>A service characterized by humility, dignity, courtesy and respect for customers, regardless of their status in life.</p>","Mission,Vision And Values","mcms","3","2017-11-14","","Active","","bps-content.php?cid=10","","","17:29:05","0","","","");
INSERT INTO c_content_master VALUES("67","Cooperate Social Responsibility","<p>As a caring organization the BPS engages in social responsibility activities to give back to the community it serves. Some of such activities are as follows:</p>\n      \n      <h3>2007</h3>\n      \n      <ul class=\"listing\">\n      <li>Commissioner&rsquo;s donation of P10,000.00 to UCCSA Church,  Molepolole Branch.</li>\n      <li>Commissioner donation of P10,000.00 to Bathusi  Support Group, Marobela</li>\n      <li>Raising of P112,000.00 to medical expenses of  Sergeant Moseki&rsquo;s ailing child</li>\n      <li>Kutlwano   Police Station donation of groceries and P1800.00 to a home based care  colleague</li>\n      <li>SIS Selibi Phikwe P5,000.00 donation to  Masiela Trust Fund</li>\n      <li>Naledi Police donation of two blankets and  toiletry to a needy person in Old Naledi</li>\n      <li>SIS Kanye donation of six tables, six table  cloths, groceries and P400 to Kanye Home Based Care.</li>\n    </ul>\n      \n      <h3>2008</h3>\n		\n      <ul class=\"listing\">\n      <li>BPS donated shelter, clothing and household equipment to the tune of P106,000.00 to disadvantaged members of the community</li>\n      </ul>\n		\n        <p>&nbsp;</p>\n        <div class=\"well\">\n        <h4 class=\"text-center\">For more information on Cooperate Social Responsibility contact 3914941</h4>\n        </div>\n\n\n","Cooperate-Social-Responsibility","mcms","Main Pages","2017-11-14","","Active","","bps-content.php?cid=67","","","18:45:53","0","","","");
