INSERT INTO c_content_master VALUES("75","Reporting Road Accident","<h3>When reporting an accident provide the following facts:-</h3>\n<ul class=\\\"listing\\\">\n  <li>The exact location of the accident</li>\n  <li>Whether there are any deaths or injuries</li>\n  <li>Whether special assistance may be required i.e. fire brigade, breakdown vehicles, medical assistance etc</li>\n  <li>The number of vehicles involved.</li>\n  <li>The reporter must give his/her particulars (Names and addresses)   to the Police Officer receiving the report to facilitate speedy   response.</li>\n</ul>","Reporting Road Accident","mcms","42","2017-11-14","","Active","","bps-content.php?cid=75","","","19:01:48","0","","","");
