INSERT INTO c_content_master VALUES("91","Firearms Resellers","<p>\n	Firearm registration enables the acquisition of a registration certificate. A firearm licence authorizes the possession of an arm and ammunition in accordance with the <a href=\\\"http://www.laws.gov.bw/\\\">Arms and Ammunitions Act Cap 24:01</a>. The service includes:</p>\n<ul class=\\\"listing\\\">\n	<li>\n		Application for Permit to Acquire a Firearm</li>\n	<li>\n		Application for Registration Certificate</li>\n	<li>\n		Application for Arm Licence</li>\n	<li>\n		Application for Temporary Permit to Possess Arms and Ammunition (Residents)</li>\n	<li>\n		Application for Temporary Import Permit for Arms and Ammunition (Non Residents)</li>\n	<li>\n		Application for Permit to Import Ammunition (Residents)</li>\n	<li>\n		Application for Export Permit</li>\n	<li>\n		Application for Arms and Ammunition Dealer&rsquo;s Licence or its Renewal</li>\n</ul>\n<p>\n	<strong>Downloadable Form</strong><br />\n	<a href=\\\"/local/botswana-police/images/application-for-registration-certificate.pdf\\\" target=\\\"_blank\\\">APPLICATION FOR REGISTRATION CERTIFICATE.pdf</a> <img alt=\\\"pdf\\\" class=\\\"pdf\\\" src=\\\"/local/botswana-police/images/pdf.jpeg\\\" /> (88Kb)</p>\n<p>\n	<strong>Service Branches: </strong><br />\n	You may locate the service branche in charge in the &nbsp;<br />\n	<a href=\\\"http://www.gov.bw/en/Ministries--Authorities/Ministries/State-President/Botswana-Police-Service-/Tools-and-Services/Ministry-Directory/?FromPageID=1590&amp;FromPageType=1&amp;pid=1&amp;ClearSearch=true&amp;Branch=-1\\\" target=\\\"_blank\\\">Useful Links</a></p>\n","Firearms Resellers","mcms","35","2017-11-14","","Active","","bps-content.php?cid=91","","","20:14:32","0","","","");
