INSERT INTO c_content_master VALUES("93","Temporary Importation of Firearms","<ul class=\\\"listing\\\">\n	<li>\n		Complete an application form (Form No.6) at the nearest Police Station;</li>\n	<li>\n		Submit copy of Registration Certificate for verification of the type, caliber and serial number.</li>\n	<li>\n		Applicant is required to be 18 years of age or above.</li>\n	<li>\n		Attach copy of Passport to the application.</li>\n	<li>\n		Where Ammunition is involved, state its number.</li>\n	<li>\n		Acquire a Temporary Permit (Filled Form No.8 - valid 30 calendar days) from date of entry from the Station Commander.</li>\n</ul>\n<p>\n	<strong>Can I apply for the service online</strong></p>\n<p>\n	Currently there is no provision for online service</p>\n<p>\n	<strong>&nbsp;</strong></p>\n<p>\n	<strong>Cost involved in obtaining the service</strong></p>\n<ul class=\\\"listing\\\">\n	<li>\n		The service is currently P100-00.</li>\n	<li>\n		The Act is under review to have the nominal fee increased</li>\n</ul>\n<p>\n	<strong>Downloadable Form</strong><br />\n	<a href=\\\"/local/botswana-police/images/application-for-temporary-import-permit-_non-resident_.pdf\\\" target=\\\"_blank\\\">APPLICATION FOR TEMPORARY IMPORT PERMIT _NON-RESIDENT_.pdf</a> <img alt=\\\"pdf\\\" class=\\\"pdf\\\" src=\\\"/local/botswana-police/images/pdf.jpeg\\\" /> (88Kb)</p>\n<p>\n	<strong>Fee: </strong>P 100.00</p>\n<p>\n	<strong>Service Branches: </strong><br />\n	You may locate the service branche in charge in the &nbsp;<a href=\\\"http://www.gov.bw/en/Ministries--Authorities/Ministries/State-President/Botswana-Police-Service-/Tools-and-Services/Ministry-Directory/?FromPageID=1590&amp;FromPageType=1&amp;pid=1&amp;ClearSearch=true&amp;Branch=-1\\\" target=\\\"_blank\\\">Useful Links</a></p>\n","Temporary Importation of Firearms","mcms","35","2017-11-14","","Active","","bps-content.php?cid=93","","","20:17:33","0","","","");
