INSERT INTO c_content_master_old VALUES("492","BPS Strategic Elements","<h3>\n	MANDATE</h3>\n<p>\n	The BPS shall be employed in and throughout Botswana to protect life and property, prevent and detect crime, repress internal disturbances, maintain security and public tranquillity, apprehend offenders, bring offenders to justice, duly enforce all written laws with which it is directly charged and generally maintain the peace (Botswana Police Act, Cap 21:01, section 6).</p>\n<p>\n	&nbsp;</p>\n<h3>\n	Our Vision:</h3>\n<h3>\n	To be a law enforcement agency of repute</h3>\n<p>\n	Aspiration to provide a dignified service worthy of honour, a service that value relations with members of the community and treats customers with due respect</p>\n<p>\n	&nbsp;</p>\n<h3>\n	Our Mission:</h3>\n<h3>\n	To provide a professional policing service in partnership with the community</h3>\n<p>\n	A<strong> </strong>service characterised by a high level of skill, competence, speed and attention to any emerging policing challenges, and that recognizes community participation for better prevention and detection of crime.&nbsp; <strong> </strong></p>\n<p>\n	&nbsp;</p>\n<h3>\n	Our Core Values</h3>\n<p>\n	<strong>&bull; Botho: </strong><br />\n	A service characterized by humility, dignity, courtesy and respect for customers, regardless of their status in life.</p>\n<p>\n	<strong>&bull; Excellence </strong>&nbsp;&nbsp;&nbsp;&nbsp;<br />\n	An acceptable level of accuracy on all services provided to customers.</p>\n<p>\n	<strong>&bull; Integrity &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><br />\n	Displaying ethical behavior that recognizes the need for confidentiality, respect for human rights and good practice</p>\n<p>\n	<strong>&bull; Team work</strong><br />\n	A service characterized by consultation, collective responsibility and support for each other. <strong> </strong></p>\n<p>\n	&nbsp;</p>\n","BPS Strategic Elements","mcms","1","2016-03-09","","","","bps-content.php?cid=492","","","12:58:01","378","","","");
