INSERT INTO c_content_master_old VALUES("557","Neighbourhood Watch ","<h3>\n	BPS is committed to ensuring that the community remains safe and secure. Tips for Neighbourhood watch</h3>\n<ul class=\\\"listing\\\">\n	<li>\n		Form or join a neighbourhood watch scheme in your area</li>\n	<li>\n		Keep your surroundings clear of bushes</li>\n	<li>\n		Report faulty street lights to relevant authorities</li>\n	<li>\n		Mark your valuable property</li>\n	<li>\n		Report suspicious characters to the police</li>\n	<li>\n		Note the telephones or cellphones of your neighbours so that you may call them should the need arise</li>\n</ul>\n","Neighbourhood Watch ","mcms","554","2017-11-13","","Active","","bps-content.php?cid=557","","","19:20:51","0","","","");
